mapper.select:
	select ano, diaryMonth, diaryDay, diaryQuestion, diaryQuestionEng, q.user_id, q.regDate, AES_DECRYPT(UNHEX(user_email),'e') as user_email, AES_DECRYPT(UNHEX(user_phone),'p') as user_phone
	from board.tbl_applyQuestion AS q LEFT JOIN board.tbl_user AS u ON q.user_id = u.user_id
	where isUse = 1

	  AND diaryMonth = #{keyword}

	order by ano desc
  limit #{displayPost}, #{postNum}

select qno, diaryMonth, diaryDay
from board.tbl_diaryQuestion
where diaryMonth = #{diaryMonth}
